import { Component, EventEmitter, OnInit,Output,Input } from '@angular/core';
import { Observable } from 'rxjs';
import { BasketService } from 'src/app/basket/basket.service';
import { IBasket, IBasketItem } from '../../Models/basket';

@Component({
  selector: 'app-basket-summery',
  templateUrl: './basket-summery.component.html',
  styleUrls: ['./basket-summery.component.scss']
})
export class BasketSummeryComponent implements OnInit {

  basket$:Observable<IBasket>;
  @Output() decrement:EventEmitter<IBasketItem> = new EventEmitter<IBasketItem>();
  @Output() increment:EventEmitter<IBasketItem> = new EventEmitter<IBasketItem>();
  @Output() remove:EventEmitter<IBasketItem> = new EventEmitter<IBasketItem>();
  @Input() isBasket:boolean = true;
  constructor(private basketService:BasketService) { }

  ngOnInit(): void {
    this.basket$= this.basketService.basket$;
  }
  decrementBasketItemQuantity(item:IBasketItem){
    this.decrement.emit(item);
  }
  incrementBasketItemQuantity(item:IBasketItem){
    this.increment.emit(item);
  }
  removeBasketItem(item:IBasketItem){
    this.remove.emit(item);
  }

}

<ng-container *ngIf="(basket$ | async)">
    <div class="table-responsive">
        <table class="table">
            <thead class="m-2" [class.bg-light]="isBasket">
                <tr class="text-uppercase py-4">
                    <th scope="col">Product</th>
                    <th scope="col">Price</th>
                    <th scope="col">Quantity</th>
                    <th scope="col">Total</th>
                    <th *ngIf="isBasket" scope="col">Remove</th>
                </tr>
            </thead>
            <tbody>
                <tr *ngFor="let item of (basket$ | async).basketItems">
                    <th scope="row">
                        <div class="p-2">
    
                            <img src="{{item.productPicture}}" alt="{{item.productName}}"
                                style="max-height: 70px;">
                            <div class="mr-3 d-inline-block align-middle">
                                <h6 class="mb-0">
                                    <a style="text-decoration: none;" class="text-danger"
                                        routerLink="/shop/{{item.id}}"> {{item.productName}}</a>
                                </h6>
                                <span class="text-muted" style="font-style:italic;">
                                    category: {{item.category}}
                                </span>
                            </div>
                        </div>
                    </th>
                    <td class="align-middle"> <strong>{{item.price | currency}}</strong> </td>
                    <td class="align-middle">
                        <div class="d-flex align-items-center" [class.justify-content-center]="!isBasket">
                            <i *ngIf="isBasket" (click)="decrementBasketItemQuantity(item)"
                                class="fa fa-minus-square text-danger mx-2" style="cursor: pointer;"></i>
                            <span style="font-weight: bold; font-size: 1em;">{{item.quantity}}</span>
                            <i *ngIf="isBasket" (click)="incrementBasketItemQuantity(item)"
                                class="fa fa-plus-square text-danger mx-2" style="cursor: pointer;"></i>
                        </div>
                    </td>
    
                    <td class="align-middle">
                        <strong>{{item.price * item.quantity | currency}} </strong>
                    </td>
                    <td class="align-middle text-center">
                        <a class="text-danger">
                            <i *ngIf="isBasket" (click)="removeBasketItem(item)" class="fa fa-trash-o"
                                style="font-size: 1.5em;cursor: pointer;"></i>
                        </a>
                    </td>
                </tr>
    
            </tbody>
        </table>
    </div>
    
</ng-container>
